INSERT INTO `cirrnainfo_disease` VALUES ('25', 'pulmonary tuberculosis', 'pulmonary tuberculosis', ' DOID:2957', ' \"A tuberculosis that is a contagious disease located_in lungs. The infection has_symptom fever, has_symptom cough, has_symptom difficulty in breathing, has_symptom inflammatory infiltrations, has_symptom formation of tubercles, has_symptom caseation, has_symptom pleural effusion, and has_symptom fibrosis.\" [url:http://www.cdc.gov/tb/publications/factsheets/general/tb.htm, url:http://www2.merriam-webster.com/cgi-bin/mwmednlm?book=Medical&va=tuberculosis]', ' ICD10CM:A15<> ICD10CM:A15.0<> ICD9CM:011<> ICD9CM:011.9<> ICD9CM:011.90<> MESH:D014397<> NCI:C26899<> SNOMEDCT_US_2016_03_01:154283005<> SNOMEDCT_US_2016_03_01:186180006<> SNOMEDCT_US_2016_03_01:397190009<> SNOMEDCT_US_2016_03_01:700272008<> SNOMEDCT_US_2016_03_01:81483001<> UMLS_CUI:C0041327', '', 'gram-positive_bacterial_infectious_disease', '', ' DOID:399 ! tuberculosis');
